function initSpaceShooter(cv,ctx,onWin,onLose){let p={x:50,y:300},bs=[],es=[],boss=null,fr=0,hp=3,wav=1,run=true;cv.onmousemove=e=>{p.y=e.offsetY};function loop(){if(!run)return;ctx.fillStyle='#000';ctx.fillRect(0,0,800,600);ctx.fillStyle='#0f0';ctx.fillRect(p.x,p.y,30,30);if(fr%20==0)bs.push({x:p.x+30,y:p.y+10});ctx.fillStyle='#ff0';bs.forEach((b,i)=>{b.x+=10;ctx.fillRect(b.x,b.y,10,5);if(b.x>800)bs.splice(i,1)});if(wav<3&&fr%60==0)es.push({x:800,y:Math.random()*550,hp:1});if(wav==3&&!boss)boss={x:700,y:300,hp:15,d:2};if(boss){boss.y+=boss.d;if(boss.y<0||boss.y>540)boss.d*=-1;ctx.fillStyle='#f00';ctx.fillRect(boss.x,boss.y,60,60);bs.forEach((b,i)=>{if(b.x>boss.x&&b.x<boss.x+60&&b.y>boss.y&&b.y<boss.y+60){boss.hp--;bs.splice(i,1);if(boss.hp<=0){run=false;onWin()}}})}es.forEach((e,i)=>{e.x-=4;ctx.fillStyle='#888';ctx.fillRect(e.x,e.y,30,30);bs.forEach((b,j)=>{if(b.x>e.x&&b.x<e.x+30&&b.y>e.y&&b.y<e.y+30){es.splice(i,1);bs.splice(j,1)}});if(e.x<0){hp--;es.splice(i,1)}});if(fr>400&&wav==1)wav=2;if(fr>800&&wav==2)wav=3;if(hp<=0){run=false;onLose()}fr++;requestAnimationFrame(loop)}loop();}
