function initMemory(cv,ctx,onWin,onLose){let seq=[],uSeq=[],cols=['#f00','#0f0','#00f','#ff0'],run=true,show=true,idx=0;for(let i=0;i<4;i++)seq.push(Math.floor(Math.random()*4));let tm=setInterval(()=>{if(!run)return clearInterval(tm);ctx.fillStyle='#000';ctx.fillRect(0,0,800,600);[0,1,2,3].forEach(i=>{let x=(i%2)*200+200,y=Math.floor(i/2)*200+100;ctx.fillStyle=cols[i];ctx.globalAlpha=(show&&seq[idx]==i)?1:0.3;ctx.fillRect(x,y,180,180)});show=!show;if(!show)idx++;if(idx>=4){clearInterval(tm);ctx.globalAlpha=1;draw();ctx.fillStyle='#fff';ctx.font="30px Poppins";ctx.fillText("–ü–û–í–¢–û–†–ò!",350,50);cv.onclick=click}},600);function draw(){[0,1,2,3].forEach(i=>{let x=(i%2)*200+200,y=Math.floor(i/2)*200+100;ctx.fillStyle=cols[i];ctx.globalAlpha=0.5;ctx.fillRect(x,y,180,180)})}function click(e){if(!run)return;let k=-1,mx=e.offsetX,my=e.offsetY;if(mx>200&&mx<380&&my>100&&my<280)k=0;if(mx>400&&mx<580&&my>100&&my<280)k=1;if(mx>200&&mx<380&&my>300&&my<480)k=2;if(mx>400&&mx<580&&my>300&&my<480)k=3;if(k!=-1){if(k==seq[uSeq.length]){uSeq.push(k);if(uSeq.length==4){run=false;onWin()}}else{run=false;onLose()}}}}
