function giveSecondKey(){gameState.hasKey2=true;gameState.ending=1;id('bossArena').style.display='none';showLegendScreen();saveGame();}
function showLegendScreen(){id('legendOverlay').style.display='flex';id('game').classList.add('game-hidden');id('legendFront').style.display='block';id('legendBack').style.display='none';const a=id('paperSwipeArea');a.onmousedown=e=>{a.sx=e.clientX};a.onmouseup=e=>{if(a.sx-e.clientX>50)flipLegend(true)};a.ontouchstart=e=>{a.sx=e.touches[0].clientX};a.ontouchend=e=>{if(a.sx-e.changedTouches[0].clientX>50)flipLegend(true)};}
function flipLegend(back){if(back){id('legendFront').style.display='none';id('legendBack').style.display='block';let c="";const ch="ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789";for(let i=0;i<10;i++)c+=ch.charAt(Math.floor(Math.random()*ch.length));secretRestoreCode=c;id('secretCodeDisplay').textContent=c}else{id('legendFront').style.display='block';id('legendBack').style.display='none'}}
function closeLegend(){id('legendOverlay').style.display='none';id('game').classList.remove('game-hidden');}
