function initTheRitual(){const c=id('clickableClock');let t=null;const start=()=>{if(gameState.hasKey1&&gameState.hasKey2&&!isTimeBroken)t=setTimeout(triggerTheEnd,2000)};const end=()=>{clearTimeout(t)};c.ontouchstart=e=>{if(e.touches.length==2)start()};c.onmousedown=e=>{if(e.buttons==3)start()};c.ontouchend=end;c.onmouseup=end;c.oncontextmenu=e=>e.preventDefault();}
function triggerTheEnd(){isTimeBroken=true;const c=id('clickableClock');c.innerHTML+=`<div class="k-ins" style="position:absolute;top:50%;left:40%;font-size:40px;">üóùÔ∏è</div><div class="k-ins" style="position:absolute;top:50%;left:60%;font-size:40px;">üóùÔ∏è</div>`;setTimeout(()=>{qa('.k-ins').forEach(k=>k.classList.add('key-hole-anim'));setTimeout(()=>{qa('.k-ins').forEach(k=>k.remove());c.classList.add('broken-clock');qa('.hand').forEach(h=>{h.style.transform='translateX(-50%) rotate(180deg)';h.style.transition='transform 1s ease-out'});realTimeFrozenValue=id('realTimePlayed').textContent;id('realTimePlayed').classList.add('stat-broken');id('realTimePlayed').onclick=startRestoreDialogue;showToast("–ß–ê–° –ó–£–ü–ò–ù–ï–ù–û...");saveGame()},2000)},1000);}
