let inpBuf="";d.addEventListener('keydown',e=>{if(id('legendBack')&&id('legendBack').style.display=='block'){inpBuf+=e.key.toUpperCase();if(inpBuf.includes(secretRestoreCode)){id('legendOverlay').style.display='none';id('restorePanel').style.display='flex';inpBuf=""}}});
